package blavatar_test

import (
	"fmt"
	"image/jpeg"
	"io/ioutil"
	"os"

	"github.com/chikamim/blavatar"
)

func ExampleNew() {
	file := "blavatar.jpg"
	img := blavatar.New("blavatar", 64)
	f, _ := os.Create(file)
	defer f.Close()
	jpeg.Encode(f, img, &jpeg.Options{70})
	got, _ := ioutil.ReadFile(file)

	fmt.Printf("%x", got)
	// Output:
	// 
}
